import React, {useContext, useEffect, useState} from 'react'
import {View, Text, Button, TouchableOpacity, Image, FlatList} from 'react-native'
import {TokenContext, UsernameContext} from "../Context/Context";
import {deleteTaskList, deleteUsers, getTaskList, users} from "../API/todoAPI";
import TodoLists from "../components/TodoLists";
//TODO pouvoir changer le slider pour tout les utilisateurs (tous marche sinon)

export default function AdminScreen () {
    const [username, setUsername] = useContext(UsernameContext)
    const [token,setToken] = useContext(TokenContext)

    const [allUsers,setAllUsers] = useState(null)
    const [nbUsers,setNbUsers] = useState(0)
    const [actualUser,setActualUser] = useState(null)

    const [todoListUser,setTodoListUser] = useState(null)
    const [nbTasksUser,setNbTasksUser] = useState(0)
    const [taskListIdUser,setTaskListIdUser] = useState(null)
    const [taskListNameUser,setTaskListNameUser] = useState(null)
    const [erreur,setErreur] = useState(null)

    useEffect(() => {
        users(username,token)
            .then(data => {
                setAllUsers(data)
                setNbUsers(data.length)
            })
            .catch(err => {
                setErreur(err.message)
            })
    },[])

    const deleteUserFunction = (id) => {
        deleteUsers(id,token)
            .then(() => {
                users(username,token)
                    .then(data => {
                        setAllUsers(data)
                        setNbUsers(data.length)
                    })
            })
    }

    const deleteTaskListFunction = (id) => {
        deleteTaskList(token,id)
            .then(() => {
                getTaskList(actualUser,token)
                    .then(data => {
                        setNbTasksUser(data.length)
                        setTodoListUser(data)
                    })
            })
    }

    return (

        taskListIdUser != null ? (
            <View>
                <Button
                    title={"Retour"}
                    onPress={() => {
                        setTaskListNameUser(null)
                        setTaskListIdUser(null)
                    }}
                />
                <Text>
                    Titre de la tasksliste: {taskListNameUser} de l'utilisateur {actualUser} avec l'id : {taskListIdUser}
                </Text>
                <TodoLists id = {taskListIdUser}/>
            </View>
        ) : (
            actualUser != null ? (
                <View>
                    <Button
                        title={"Retour"}
                        onPress={() => {
                            setActualUser(null)
                        }}
                    />
                    <Text>L'utilisateur {actualUser} poss√®de {nbTasksUser} todolists :</Text>
                    <FlatList data={todoListUser} renderItem={(item) =>
                        <View>
                            <Button
                                title={item.item.title}
                                onPress={() => {
                                    setTaskListIdUser(item.item.id)
                                    setTaskListNameUser(item.item.title)
                                }}
                            />
                            <TouchableOpacity onPress={() => deleteTaskListFunction(item.item.id)}>
                                <Image source={require('../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />
                            </TouchableOpacity>
                        </View>}>
                    </FlatList>
                </View>
            ) : (
                <View>
                    <Text>Bienvenue dans la section admin, ci-dessous dessous ce trouve tous les utilisateur de l'application</Text>
                    <Text>Il y a actuellement {nbUsers} utilisateurs inscrit sur l'application</Text>
                    <FlatList data={allUsers} renderItem={(user) =>
                        <View>
                            <Text>Nom de l'utilisateur : {user.item.username}</Text>
                            <Button
                                title="TodoLists de l'utilisateur"
                                onPress={() => {
                                    getTaskList(user.item.username,token)
                                        .then(data => {
                                            setNbTasksUser(data.length)
                                            setTodoListUser(data)
                                        })
                                        .catch(err => {
                                            setErreur(err.message)
                                        })
                                    //TODO Prendre la liste des todoliste de l'utilisteurs choisie.
                                    setActualUser(user.item.username)
                                }}
                            />
                            <TouchableOpacity onPress={() => deleteUserFunction(user.item.id)}>
                                <Image source={require('../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />
                            </TouchableOpacity>
                        </View>}>
                    </FlatList>
                </View>
            )
        )

    )
}
