import React, {useContext, useEffect} from 'react'
import {View, Text, StyleSheet} from 'react-native'
import {RoleContext, UsernameContext} from "../Context/Context";


export default function HomeScreen () {
    const [username, setUsername] = useContext(UsernameContext)
    const [role,setRole] = useContext(RoleContext)
    return (
        <View style={styles.container}>
            <Text
                style={{
                    marginBottom:60,
                    fontSize: 40,
                    fontWeight: "bold"
                }}
            >
                TodoList
            </Text>
            <Text
                style={{
                    marginBottom:10,
                    fontSize: 20,
                    fontWeight: "bold"
                }}
            >
                Bienvenue!
            </Text>
            <View style={{flexDirection: 'row',fontWeight: "bold"}}>
                <Text>Vous êtes actuellement connecter en tant que</Text>
                <Text style={{color:"#3E6259"}}> {username}</Text>
            </View>
            <Text>Votre rôle est {role}</Text>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
});
