import React, {useContext, useState} from 'react'
import {Text, Button, TextInput,View,StyleSheet} from 'react-native'
import {TokenContext, UsernameContext,RoleContext} from "../Context/Context";
import {checkRole, signIn} from "../API/todoAPI";
import {Link} from "@react-navigation/native";
//TODO ROLE
export default function SignInScreen ({ navigation }) {
    const [token, setToken] = useContext(TokenContext)
    const [user,setUser] = useContext(UsernameContext)
    const [role,setRole] = useContext(RoleContext)
    const [username,setUsername] = useState('')
    const [password,setPassword] = useState('')
    const [erreur,setErreur] = useState(null)

    return( <View style={styles.container}>
        <Text
            style={{
                marginBottom:40,
                fontSize: 40,
                fontWeight: "bold"
            }}
        >
            TodoList
        </Text>
        <Text
            style={{
                marginBottom:10,
                fontSize: 20,
                fontWeight: "bold"
            }}
        >
            Connexion
        </Text>
        {erreur != null ? <Text>{erreur}</Text> : <Text>bonjour</Text>}
        <View styles={styles.inputView}>
            <Text style={{marginBottom:5,}}>Pseudo</Text>
            <TextInput
                placeholder="Pseudo"
                onChangeText={data => setUsername(data)}
                style={styles.textInput}
            />
        </View>
        <View styles={styles.inputView}>
            <Text style={{marginBottom:5,}}>Mot de passe</Text>
            <TextInput
                secureTextEntry={true}
                onChangeText={data => setPassword(data)}
                placeholder="Mdp"
                style={styles.inputView}
            />
        </View>
        <Button
            title="Connexion"
            style={{
                width:"80%",
                borderRadius:25,
                height:50,
                alignItems:"center",
                justifyContent:"center",
                marginTop:40,
                backgroundColor:"#FF1493",
                marginBottom:20,
            }}
            onPress={() => signIn(username, password)
                .then(token => {
                    setToken(token)
                    setUser(username)
                    checkRole(username,token)
                        .then((data) => {
                            setRole(data[0].roles[0])
                        })
                        .catch((error) => {
                            setErreur(error)
                        })
                    props.navigate('Home')
                })
                .catch(err => {
                    setErreur(err.message)
                })}
        />
        <Text>
            Pas encore de compte? {' '}
            <Link
                style={{ textDecorationLine: 'underline' }}
                to={{ screen: 'SignUp' }}
            >
                Inscrivez-vous!
            </Link>
        </Text>
    </View>)
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#fff',
    },
    inputView:{
        backgroundColor: "#ff002e",
        borderRadius: 30,
        width: "70%",
        height: 45,
        marginBottom: 20,
        alignItems: "center",
    },
    textInput:{
        height: 50,
        flex: 1,
        padding: 10,
        marginBottom:10,
    }
});
