import React, {useContext, useEffect, useState} from "react";
import {View, TextInput, Button, Text, FlatList, Switch, StyleSheet} from 'react-native';
import TodoItem from './TodoItem';
import {getTasks, createTask, deleteTask} from "../API/todoAPI";
import {TokenContext, UsernameContext} from "../Context/Context";
import {TouchableOpacity} from "react-native-web";
//TODO voir le compteur (marche pas du tout)
export default function TodoLists(props){
    const [user,setUser] = useContext(UsernameContext)
    const [token,setToken] = useContext(TokenContext)
    const [count, setCount] = useState(0);
    const [todos, setTodos] = useState(null);
    const [newTodoText, setNewTodoText] = useState("");
    const [shouldShow, setShouldShow] = useState(true);
    const [shouldDone, setShouldDone] = useState(false);
    const [shouldNotDone, setShouldNotDone] = useState(false);
    const [erreur,setErreur] = useState(null);

    useEffect(() => {
        getTasks(props.id,token)
            .then(data => {
                setCount(data.filter((item)=>item.done).length)
                setTodos(data)
            })
            .catch(err => {
                setErreur(err)
            })
    },[])

    const onPressed = (offset) => {
        getTasks(props.id,token)
            .then(data => {
                setTodos(data)
                setCount(data.filter((item)=>item.done).length)
            })
            .catch(err => {
                setErreur(err)
            })
    }

    const deleteTodo = (id) => {
        deleteTask(token,id)
            .then(() => {
                getTasks(props.id,token)
                    .then(data => {
                        setTodos(data)
                        setCount(data.filter((item)=>item.done).length)
                    })
            })

    }

    const addNewTodo = () => {
        createTask(newTodoText,token,props.id)
            .then(() => {
                getTasks(props.id,token)
                    .then(data => {
                        setTodos(data)
                        setCount(data.filter((item)=>item.done).length)
                    })
            })
    }

    const checkAll = () => {
        const newTodos = todos.map(item => {return {id: item.id, content: item.content, done:true}})
        setTodos(newTodos)
        setCount(newTodos.length)
    }

    const checkNone = () => {
        setTodos(todos.map(item => {return {id: item.id, content: item.content, done:false}}))
        setCount(0)
    }

    return (
        <View style={styles.container}>
            <Text style={{
                marginTop:50,
                marginBottom:20,
                fontSize: 20,
                fontWeight: "bold"}}>
                {props.title}
            </Text>
            <Text style={{marginBottom:20}}>Il y a {count} slider a true</Text>
            {shouldShow ? (
                <FlatList
                data={todos}
                renderItem={({item}) =>
                    <TodoItem onPressed = {onPressed} item={item} deleteTodo = {deleteTodo} beDone = {shouldDone} beNotDone = {shouldNotDone} id = {item.id}/>
                }
                />
            ) : <></>}


            <Button
                title="Seulement done"
                onPress={() => setShouldDone(!shouldDone)}
            />
            <Button
                title="Seulement pas done"
                onPress={() => setShouldNotDone(!shouldNotDone)}
            />
            <Button
                title="CkeckAll"
                onPress={() => checkAll()}
            />
            <Button
                title="CkeckNone"
                onPress={() => checkNone()}
            />
            <TextInput
                onChangeText={setNewTodoText}
                placeholder='saisir ici un nouvel item'
                onSubmitEditing={addNewTodo}
                value={newTodoText}
            />
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },

    generalBtn: {
        width: "85%",
        borderRadius: 25,
        height: 35,
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#5B8266",
    },
});
