import React, {useContext, useEffect, useState} from 'react'
import {
    View,
    Text,
    FlatList,
    TextInput,
    SafeAreaView,
    Button,
    Image,
    TouchableOpacity,
    StyleSheet
} from 'react-native'
import {getTaskList, createTaskList,deleteTaskList} from "../API/todoAPI";
import {TokenContext,UsernameContext} from "../Context/Context";
import TodoLists from "../components/TodoLists";

export default function TodoListsScreen(){
    const [user,setUser] = useContext(UsernameContext)
    const [token,setToken] = useContext(TokenContext)
    const [erreur,setErreur] = useState(null)
    const [data,setData] = useState(null)
    const [nbTasks,setNbTasks] = useState(null)
    const [title,setTitle] = useState("")
    const [taskListId,setTaskListId] = useState(null)
    const [taskListTitle,setTaskListTitle] = useState(null)

    useEffect(() => {
        getTaskList(user,token)
            .then(data => {
                setNbTasks(data.length)
                setData(data)
            })
            .catch(err => {
                setErreur(err.message)
            })
    },[data])

    const addTaskList = () => {
        createTaskList(token,user,title)
            .then( () => {
                getTaskList(user,token)
                    .then(data => {
                        setNbTasks(data.length)
                        setData(data)
                    })
            })
    }

    const deleteTaskListFunction = (id) => {
        deleteTaskList(token,id)
            .then(() => {
                getTaskList(user,token)
                    .then(data => {
                        setNbTasks(data.length)
                        setData(data)
                    })
            })
    }

    return (
        taskListId != null ? (
            <View style={styles.container}>
                <Button
                    title={"Retour"}
                    onPress={() => {
                        setTaskListTitle(null)
                        setTaskListId(null)
                    }}
                />
                <Text>
                    Titre de la tasksliste: {taskListTitle}
                </Text>
                <TodoLists id = {taskListId}/>
            </View>
        ) : (
            erreur != null ? (
                <View style={styles.container}>
                    <Text>Bienvenue sans le liste des todos</Text>
                    <Text>{erreur}</Text>
                </View>
            ) : (
                <View style={styles.container}>
                    <Text style={{
                        marginBottom:10,
                        fontSize: 20,
                        fontWeight: "bold"
                    }}>Bienvenue dans le liste des todos</Text>
                    <TextInput
                        onChangeText={setTitle}
                        placeholder='saisir ici une nouvelle tasklist'
                        onSubmitEditing={addTaskList}
                        value={title}
                    />
                    <Text>Vous poss√©dez {nbTasks} tasksLists</Text>
                    <FlatList data={data} renderItem={(item) =>
                        <View>
                            <Button
                                title={item.item.title}
                                onPress={() => {
                                    setTaskListId(item.item.id)
                                    setTaskListTitle(item.item.title)
                                }}
                            />
                            <TouchableOpacity onPress={() => deleteTaskListFunction(item.item.id)}>
                                <Image source={require('../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />
                            </TouchableOpacity>
                        </View>}>
                    </FlatList>
                </View>
            )
        )
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },

    inputView: {
        backgroundColor: "#AEF6C7",
        borderRadius: 30,
        width: "70%",
        height: 45,
        marginBottom: 20,
        alignItems: "center",
    },

    TextInput: {
        height: 50,
        flex: 1,
        padding: 10,
        width: "80%",
        alignItems:"center",
    },

    loginBtn: {
        width: "80%",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 40,
        backgroundColor: "#5B8266",
        marginBottom:10
    },
});
