import React, {useContext, useEffect, useState} from 'react'
import {View, Text, Button, TouchableOpacity, Image, FlatList} from 'react-native'
import {TokenContext, UsernameContext} from "../Context/Context";
import {users} from "../API/todoAPI";


export default function AdminScreen () {
    const [username, setUsername] = useContext(UsernameContext)
    const [token,setToken] = useContext(TokenContext)
    const [allUsers,setAllUsers] = useState(null)
    const [nbUsers,setNbUsers] = useState(0)
    const [actualUser,setActualUser] = useState(null)
    const [erreur,setErreur] = useState(null)

    useEffect(() => {
        users(username,token)
            .then(data => {
                setAllUsers(data)
                setNbUsers(data.length)
            })
            .catch(err => {
                setErreur(err.message)
            })
    },[])

    return (
        <View>
            <Text>Bienvenue dans la section admin, ci-dessous dessous ce trouve tous les utilisateur de l'application</Text>
            <Text>Il y a actuellement {nbUsers} utilisateurs inscrit sur l'application</Text>
            <FlatList data={allUsers} renderItem={(user) =>
                <View>
                    <Text>Nom de l'utilisateur : {user.item.username}</Text>
                    <Button
                        title="TodoLists de l'utilisateur"
                        onPress={() => {
                            setActualUser(user.item.id)
                    }}
                        />
                    <TouchableOpacity onPress={() => deleteTaskListFunction(item.item.id)}>
                        <Image source={require('../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />
                    </TouchableOpacity>
                </View>}>
            </FlatList>
        </View>
    )
}
