import React, {useContext, useEffect, useState} from "react";
import { Image, View, Text, StyleSheet, Switch, TouchableOpacity } from 'react-native';
import {updateTaskList} from "../API/todoAPI";
import {TokenContext, UsernameContext} from "../Context/Context";

export default function TodoItem(props) {
    //console.log(props.item);
    // Pour être remis a jour, on vas utiliser useEffects

    const [user,setUser] = useContext(UsernameContext)
    const [token,setToken] = useContext(TokenContext)
    const [done, setDone] = useState(props.item.done);

    const changeDone = (state) => {
        setDone(state)
        props.onPressed(state ? 1 : -1)
        updateTaskList(token,props.id,state)
            .then(() => {
                //props.onPressed(state ? 1 : -1)
            })//TODO .then???
         // Marche pas le compteur la maxiDisco
    }

    /*useEffect(() => {
    }, [props.item.done])

     */

    if(props.beDone){ // Autre manière, mettre deux liste dans le père
        return (
            <View style={styles.content}>
                {done ? (<Switch value={done} onValueChange={(state) => {changeDone(state)}} />) : null}
                {done ? (<Text style={[styles.text_item, { textDecorationLine: done ? 'line-through' : 'none' }]}>{props.item.content}</Text>) : null}
                {done ? (<TouchableOpacity onPress={() => props.deleteTodo(props.item.id)}>
                    <Image source={require('../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />
                </TouchableOpacity>) : null}

            </View>
        )
    }
    else if(props.beNotDone){
        return (
            <View style={styles.content}>
                {!done ? (<Switch value={done} onValueChange={(state) => {changeDone(state)}} />) : null}
                {!done ? (<Text style={[styles.text_item, { textDecorationLine: done ? 'line-through' : 'none' }]}>{props.item.content}</Text>) : null}
                {!done ? (<TouchableOpacity onPress={() => props.deleteTodo(props.item.id)}>
                    <Image source={require('../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />
                </TouchableOpacity>) : null}

            </View>
        )
    }
    else{
       return (
           <View style={styles.content}>
               <Switch value={done} onValueChange={(state) => {changeDone(state)}} />
               <Text style={[styles.text_item, { textDecorationLine: done ? 'line-through' : 'none' }]}>{props.item.content}</Text>
               <TouchableOpacity onPress={() => props.deleteTodo(props.item.id)}>
                   <Image source={require('../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />
               </TouchableOpacity>

           </View>
       )
    }

}

const styles = StyleSheet.create({
    content: {
        flexDirection: 'row'
    },
    text_item: {
        marginLeft: 10,
        width: 150
    }
})
