import React, {useContext, useEffect, useState} from 'react'
import {View, Text, FlatList, TextInput, ScrollView, SafeAreaView, Button} from 'react-native'
import {getTaskList, createTaskList} from "../API/todoAPI";
import {TokenContext,UsernameContext} from "../Context/Context";
import TodoLists from "./TodoLists";
//TODO mieux afficher les listes de todo

export default function TodoListsScreen(){
    const [user,setUser] = useContext(UsernameContext)
    const [token,setToken] = useContext(TokenContext)
    const [erreur,setErreur] = useState(null)
    const [data,setData] = useState(null)
    const [nbTasks,setNbTasks] = useState(null)
    const [title,setTitle] = useState("")
    const [taskList,setTaskList] = useState(null)

    useEffect(() => {
        getTaskList(user,token)
            .then(data => {
                setNbTasks(data.length)
                setData(data)
            })
            .catch(err => {
                setErreur(err.message)
            })
    },[])

    const addTaskList = () => {
        createTaskList(token,user,title)
            .then( () => {
                getTaskList(user,token)
                    .then(data => {
                        setNbTasks(data.length)
                        setData(data)
                    })
            })
    }

    return (
        taskList != null ? (
            <View>
                <Text>
                    Bonjour tout le monde
                </Text>
            </View>
        ) : (
            erreur != null ? (
                <View>
                    <Text>Bienvenue sans le liste des todos</Text>
                    <Text>{erreur}</Text>
                </View>
            ) : (
                <SafeAreaView>
                    <Text>Bienvenue sans le liste des todos</Text>
                    <TextInput
                        onChangeText={setTitle}
                        placeholder='saisir ici une nouvelle tasklist'
                        onSubmitEditing={addTaskList}
                        value={title}
                    />
                    <Text>Vous poss√©dez {nbTasks} tasksLists</Text>
                    <FlatList data={data} renderItem={(item) =>
                        <View>
                            <Button
                                title={item.item.id}
                                onPress={() => setTaskList(item.item.title)}
                            />
                        </View>}>
                    </FlatList>
                </SafeAreaView>
            )
        )
    )

}
