import React, {useContext, useEffect, useState} from "react";
import {View, TextInput, Button, Text, FlatList, Switch } from 'react-native';
import TodoItem from './TodoItem';
import {getTasks, createTask, deleteTask} from "../API/todoAPI";
import {TokenContext, UsernameContext} from "../Context/Context";
//TODO voir le compteur (marche pas du tout)
export default function TodoLists(props){
    const [user,setUser] = useContext(UsernameContext)
    const [token,setToken] = useContext(TokenContext)
    const [count, setCount] = useState(0);
    const [todos, setTodos] = useState(null);
    const [newTodoText, setNewTodoText] = useState("");
    const [shouldShow, setShouldShow] = useState(true);
    const [shouldDone, setShouldDone] = useState(false);
    const [shouldNotDone, setShouldNotDone] = useState(false);
    const [erreur,setErreur] = useState(null);

    useEffect(() => {
        getTasks(props.id,token)
            .then(data => {
                setTodos(data)
                //setCount(todos.filter((item)=>item.done).length)
            })
            .catch(err => {
                setErreur(err)
            })
    },[])

    const onPressed = (offset) => {
        setCount(count + offset)
    }

    const deleteTodo = (id) => {
        deleteTask(token,id)
            .then(() => {
                getTasks(props.id,token)
                    .then(data => {
                        setTodos(data)
                        setCount(todos.filter((item)=>item.done).length)
                    })
            })

    }

    const addNewTodo = () => {
        createTask(newTodoText,token,props.id)
            .then(() => {
                getTasks(props.id,token)
                    .then(data => {
                        setTodos(data)
                        setCount(todos.filter((item)=>item.done).length)
                    })
            })

    }

    const checkAll = () => {
        setTodos(todos.map(item => {return {id: item.id, content: item.content, done:true}}))
        //setCount(todos.length)
    }

    const checkNone = () => {
        setTodos(todos.map(item => {return {id: item.id, content: item.content, done:false}}))
        //setCount(0)
    }

    return (
        <View>
            {shouldShow ? (<FlatList
                style={{ paddingTop:125}}
                data={todos}
                renderItem={({item}) => <TodoItem onPressed = {onPressed} item={item} deleteTodo = {deleteTodo} beDone = {shouldDone} beNotDone = {shouldNotDone} id = {item.id}/>} />) : null}
            <Button
                title="Tout afficher/ Tout retirer"
                onPress={() => setShouldShow(!shouldShow)}
            />
            <Button
                title="Seulement done"
                onPress={() => setShouldDone(!shouldDone)}
            />
            <Button
                title="Seulement pas done"
                onPress={() => setShouldNotDone(!shouldNotDone)}
            />
            <Button
                title="CkeckAll"
                onPress={() => checkAll()}
            />
            <Button
                title="CkeckNone"
                onPress={() => checkNone()}
            />
            <Text>Il y a {count} slider a true</Text>
            <TextInput
                onChangeText={setNewTodoText}
                placeholder='saisir ici un nouvel item'
                onSubmitEditing={addNewTodo}
                value={newTodoText}
            />
        </View>
    )
}
