import React, {useContext, useEffect} from 'react'
import {View, Text, StyleSheet} from 'react-native'
import {RoleContext, UsernameContext} from "../Context/Context";


export default function HomeScreen () {
    const [username, setUsername] = useContext(UsernameContext)
    const [role,setRole] = useContext(RoleContext)
    return (
        <View style={styles.container}>
            <Text
                style={{
                    marginBottom:60,
                    fontSize: 40,
                    fontWeight: "bold"
                }}
            >
                TodoList
            </Text>
            <Text
                style={{
                    marginBottom:10,
                    fontSize: 20,
                    fontWeight: "bold"
                }}
            >
                Bienvenue!
            </Text>
            <Text>You are logged as {username}</Text>
            <Text>Your role is {role}</Text>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },

    inputView: {
        backgroundColor: "#AEF6C7",
        borderRadius: 30,
        width: "70%",
        height: 45,
        marginBottom: 20,
        alignItems: "center",
    },

    TextInput: {
        height: 50,
        flex: 1,
        padding: 10,
        width: "80%",
        alignItems:"center",
    },

    loginBtn: {
        width: "80%",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 40,
        backgroundColor: "#5B8266",
        marginBottom:10
    },
});
