import React, {useContext, useState} from 'react'
import {Text, Button, TextInput,View} from 'react-native'
import {TokenContext, UsernameContext,RoleContext} from "../Context/Context";
import {checkRole, signIn} from "../API/todoAPI";
import {Link} from "@react-navigation/native";
//TODO ROLE
export default function SignInScreen ({ navigation }) {
    const [token, setToken] = useContext(TokenContext)
    const [user,setUser] = useContext(UsernameContext)
    const [role,setRole] = useContext(RoleContext)
    const [username,setUsername] = useState('')
    const [password,setPassword] = useState('')
    const [erreur,setErreur] = useState(null)
    return( <View
        style={{
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: '#fff',
        }}
         >
        <Text
            style={{
                marginBottom:40,
                fontSize: 40,
                fontWeight: "bold"
            }}
        >
            TodoList
        </Text>
        <Text
            style={{
                marginBottom:10,
                fontSize: 20,
                fontWeight: "bold"
            }}
        >
            Connexion
        </Text>
        {erreur != null ? (<Text>{erreur}</Text>) : (<Text></Text>)}
        <Text
            style={{
                marginBottom:5,
            }}
        >
            Pseudo
        </Text>
        <TextInput
            placeholder="Pseudo"
            onChangeText={data => setUsername(data)}
            style={{
                marginBottom:10,
            }}
        />
        <Text>Mdp:</Text>
        <TextInput
            secureTextEntry={true}
            onChangeText={data => setPassword(data)}
            placeholder="Mdp"
        />
        <Button
            title="Connexion"
            onPress={() => signIn(username, password)
                .then(token => {
                    setToken(token)
                    setUser(username)
                    checkRole(username,token)
                        .then((data) => {
                            setRole(data[0].roles[0])
                            //console.log(data[0].roles[0])
                        })
                        .catch((error) => {
                            setErreur(error)
                        })
                    props.navigate('Home')
                })
                .catch(err => {
                    setErreur(err.message)
                })}
        />
        <Text>
            If you prefer, you can{' '}
            <Link
                style={{ textDecorationLine: 'underline' }}
                to={{ screen: 'SignUp' }}
            >
                Sign Up
            </Link>
        </Text>
    </View>)
}
